// Amended by HashLips

pragma solidity >=0.7.0 <0.9.0;





contract NFT is ERC721Enumerable,Ownable ,ERC2981 {

  using Strings for uint256;
  struct Mint {
        address owner;
        uint256 amount;
        uint256 born_date;
        uint256 death_time;
        uint256 growth_cooldown;     
  }
  mapping(uint256 => Mint) public Mint_vault; 


  uint96    royaltyFeesInBips;
  address   royaltyAddress;
  string    public baseURI;
  string    public deadURI;
  string    public baseExtension = ".json";
  uint256   public cost = 0.15 ether;
  uint256   public reviveCost = 0.1 ether;
  uint256   public maxSupply = 1000;     //maximum supply of the nfts 
  uint256   public maxMintAmount = 2;   //maximum mint amount per transaction 
  uint256   public nftPerAddressLimit = 2; // maximum number of nfts that the user can mint 
  uint256   public rate = 10; //rate of shibv2 tokens for calling the timeincrease function 
  uint256   public timeincrease = 4 days;   // lifetime increase for the nft before dying . 
  uint256   public growthcooldown = 2 days; // growth function cooldown time 
  uint256   public baseLife = 10 days;
  bool      public paused = false;
  bool      public onlyWhitelisted = false; 
  mapping(address => uint256) public addressMintedBalance;
  mapping(address => bool) public whitelist;
  IERC20 public tokenAddress;

  constructor(
    string memory _name,
    string memory _symbol,
    string memory _initBaseURI,
    string memory _initDeadURI,
    uint96 _royaltyFeesInBips,
    address _tokenAddress
  ) ERC721(_name, _symbol) {
    setRoyaltyInfo(msg.sender, _royaltyFeesInBips);
    setBaseURI(_initBaseURI);
    setDeadURI(_initDeadURI);
    tokenAddress = IERC20(_tokenAddress);
  }

  function _baseURI() internal view virtual override returns (string memory) {
    return baseURI;
    }

  function setFeedToken(address _newToken) public onlyOwner {
    tokenAddress = IERC20(_newToken);
  }

  function setRoyaltyInfo(address _receiver, uint96 _royaltyFeesInBips) public onlyOwner {
        _setDefaultRoyalty(_receiver, _royaltyFeesInBips);
    }  

  function mint(uint256 _mintAmount) public payable {     
    require(!paused, "the contract is paused");
    uint256 supply = totalSupply();
    require(_mintAmount > 0, "need to mint at least 1 NFT");
    require(_mintAmount <= maxMintAmount, "max mint amount per session exceeded");
    require(supply + _mintAmount <= maxSupply, "max NFT limit exceeded");

    if (msg.sender != owner()) {
        if(onlyWhitelisted == true) {
            require(whitelist[msg.sender], "User not Whitelisted");
            uint256 ownerMintedCount = addressMintedBalance[msg.sender];
            require(ownerMintedCount + _mintAmount <= nftPerAddressLimit, "max NFT per address exceeded");
        }
        require(msg.value >= cost * _mintAmount, "insufficient funds");
    }  
    for (uint256 i = 1; i <= _mintAmount; i++) {
        addressMintedBalance[msg.sender]++;
      _safeMint(msg.sender, supply + i);
    Mint_vault[supply+i] = Mint({
        owner: msg.sender,
        amount: _mintAmount,
        born_date: uint48(block.timestamp),
        death_time: uint48(block.timestamp + baseLife),
        growth_cooldown : 0 
      });
    } 
  }

  function addToWhitelist(address[] calldata toAddAddresses) external onlyOwner
    {
        for (uint i = 0; i < toAddAddresses.length; i++) {
            whitelist[toAddAddresses[i]] = true;
        }
    }

  function removeFromWhitelist(address[] calldata toRemoveAddresses) external onlyOwner
    {
        for (uint i = 0; i < toRemoveAddresses.length; i++) {
            delete whitelist[toRemoveAddresses[i]];
        }
    }

  function isWhitelisted(address _user) public view returns (bool) {
    if (whitelist[_user] == true) {
          return true;
      }
    return false;
  }

  function walletOfOwner(address _owner)
    public
    view
    returns (uint256[] memory)
  {
    uint256 ownerTokenCount = balanceOf(_owner);
    uint256[] memory tokenIds = new uint256[](ownerTokenCount);
    for (uint256 i; i < ownerTokenCount; i++) {
      tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
    }
    return tokenIds;
  }

  function tokenURI(uint256 tokenId)
    public
    view
    virtual
    override
    returns (string memory)
  {
    require(
      _exists(tokenId),
      "ERC721Metadata: URI query for nonexistent token"
    );
    if(block.timestamp < Mint_vault[tokenId].death_time) {
      string memory currentBaseURI = _baseURI();
      return bytes(currentBaseURI).length > 0
        ? string(abi.encodePacked(currentBaseURI, tokenId.toString(), baseExtension))
        : "";
    }
    else {
      return deadURI;
    }
    
  }

  function setNftPerAddressLimit(uint256 _limit) public onlyOwner {
    nftPerAddressLimit = _limit;
  }

  function setFeedingTokenPrice(uint256 price) public onlyOwner {
    rate = price;
  }
  
  function setbaseLifeSecondsInput(uint256 _baseLife) public onlyOwner {
    baseLife = _baseLife;
  }

  function getbaseLife() external view returns (uint256) {
        return baseLife;
    }

   function setGrowthCooldownSecondsInput(uint256 _growthcooldown) public onlyOwner {
    growthcooldown = _growthcooldown;
  }

  function getGrowthCooldown() external view returns (uint256) {
        return growthcooldown;
    }

  function setCost(uint256 _newCost) public onlyOwner {
    cost = _newCost;
  }

  function setReviveCost(uint256 _newCost) public onlyOwner {
    reviveCost = _newCost;
  }

  function setmaxMintAmount(uint256 _newmaxMintAmount) public onlyOwner {
    maxMintAmount = _newmaxMintAmount;
  }

  function setBaseURI(string memory _newBaseURI) public onlyOwner {
    baseURI = _newBaseURI;
  }

  function setDeadURI(string memory _newDeadURI) public onlyOwner {
    deadURI = _newDeadURI;
  }
 
  function setBaseExtension(string memory _newBaseExtension) public onlyOwner {
    baseExtension = _newBaseExtension;
  }

  function pause(bool _state) public onlyOwner {
    paused = _state;
  }
  
  function setOnlyWhitelisted(bool _state) public onlyOwner {
    onlyWhitelisted = _state;
  }
 
  function withdraw() public payable onlyOwner {
    (bool os, ) = payable(owner()).call{value: address(this).balance}("");
    require(os);
  }

  function _beforeTokenTransfer(address from, address to, uint256 tokenId)
        internal
        override( ERC721Enumerable)
    {
      if( to == address(0) )
      { 
        super._beforeTokenTransfer(from, to, tokenId);
      }
      if( Mint_vault[tokenId].death_time == 0 )
      { 
        super._beforeTokenTransfer(from, to, tokenId);
      }
      else{
        require(block.timestamp < Mint_vault[tokenId].death_time, "The Token Just got Buried ! RIP :D ");
        super._beforeTokenTransfer(from, to, tokenId);
      }
    }

  function performRitual(uint24 timechanger) public payable {
    if (msg.sender != owner()) {
      require(msg.value >= reviveCost, "Insufficient Funds");
    }
    Mint_vault[timechanger].death_time = block.timestamp + timeincrease; 
  }

  function killNft(uint24 timechanger) public onlyOwner {
    Mint_vault[timechanger].death_time = block.timestamp - timeincrease; 
  }

  function claimOtherTokens(IERC20 _tokenAddress, address _walletaddress) external onlyOwner {
        _tokenAddress.transfer(_walletaddress, _tokenAddress.balanceOf(address(this)));
  }
  
  function feedNFT(uint24 timechanger) public {
        require(Mint_vault[timechanger].owner == msg.sender , "You are not the owner of this Token ! ");
        require(block.timestamp < Mint_vault[timechanger].death_time, "The Token Already Died ! RIP :D ");
        
        if(Mint_vault[timechanger].growth_cooldown == 0)
        {
          tokenAddress.transferFrom(msg.sender, address(this), rate);
          Mint_vault[timechanger].death_time = Mint_vault[timechanger].death_time + timeincrease; 
          Mint_vault[timechanger].growth_cooldown = block.timestamp + growthcooldown;
        }
        else
        {
          require(block.timestamp > Mint_vault[timechanger].growth_cooldown, "Cannot Use The Function Before 7 Days Cooldown");
          tokenAddress.transferFrom(msg.sender, address(this), rate);
          Mint_vault[timechanger].death_time = Mint_vault[timechanger].death_time + timeincrease; 
          Mint_vault[timechanger].growth_cooldown = block.timestamp + growthcooldown;
        }
    }   
      function supportsInterface(bytes4 interfaceId)
        public
        view
        override( ERC721Enumerable, ERC2981)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }     
}

