        uint256 remainingETHBalance = address(this).balance;
        processNOS(remainingETHBalance,NOS);
        emit tokensSwappedDuringTokenomics(balanceToswap);
        mktTokens = 0;
        NOSTokens = 0;
        liqTokens = 0;
