//Tokenomics related functions
function COINDay() public onlyOwner{
    require(block.timestamp > DOGELFGCOINDaycooldown, "You cant call DOGELFGCOINCoinDay more than once a day");
    buyPrevmktFee = buymktFee;
    buyprevLiqFee = buyliqFee;
    buyliqFee = 0;
    buymktFee = 0;
}
function DOGELFGCOINCoinDayOver() public onlyOwner{
    buyliqFee = buyprevLiqFee;
    buymktFee = buyPrevmktFee;
    DOGELFGCOINDaycooldown = block.timestamp + 86400;
