contract FifaBet is Ownable {
    using SafeERC20 for IERC20;

    // Whether it is initialized
    bool public isInitialized;

    // The staked token
    IERC20 public Token;
    uint256 public totalStaked;
    uint256 public totalCount;
    mapping(uint256 => bool) public isCreated;

    address[] public addressList;
    uint256[] public bracketsList;

    struct BracketInfo {
        uint256[] bracketList;
    }

    mapping(address => BracketInfo) userBracket;

    event CreateBracket(address indexed user, uint256 amount, uint256 bracket);
    event ChooseWinner(address winner, uint256 amount);
    event AdminTokenRecovered(address tokenRecovered, uint256 amount);

    constructor() {}

    /*
     * @notice Initialize the contract
     * @param _Token: staked token address
     * @param _earnedToken: earned token address
     */
    function initialize(
        IERC20 _Token
    ) external onlyOwner {
        require(!isInitialized, "Already initialized");

        // Make this contract initialized
        isInitialized = true;

        Token = _Token;
    }

    function createBracket(uint256 _amount, uint256 bracket) external {
        require(_amount == 1000*10**18, "Amount should be greator than 0");
        require(isCreated[bracket] == false, "Same Bracket is already created");
        require(userBracket[address(msg.sender)].bracketList.length < 10, "User limited MAX_COUNT");

        isCreated[bracket] = true;

        userBracket[address(msg.sender)].bracketList.push(bracket);

        addressList.push(address(msg.sender));

        bracketsList.push(bracket);

        Token.safeTransferFrom(address(msg.sender), address(this), _amount);
        
        totalStaked = totalStaked + _amount;

        totalCount += 1;

        emit CreateBracket(msg.sender, _amount, bracket);
    }

    function userInfo(address account) external view returns(uint256[] memory) {
        BracketInfo memory bracketList = userBracket[account];
        return bracketList.bracketList;
    }


    /************************
    ** Admin Methods
    *************************/
   
    /*
     * @notice Deposit reward token
     * @dev Only call by owner. Needs to be for deposit of reward token when reflection token is same with reward token.
     */
    function chooseWinner(address account) external onlyOwner {
        Token.safeTransfer(address(account), totalStaked);
        emit ChooseWinner(address(account), totalStaked);
    }

    /**
     * @notice It allows the admin to recover wrong tokens sent to the contract
     * @param _tokenAddress: the address of the token to withdraw
     * @param _tokenAmount: the number of tokens to withdraw
     * @dev This function is only callable by admin.
     */
    function recoverWrongTokens(address _tokenAddress, uint256 _tokenAmount) external onlyOwner {
        if(_tokenAddress == address(0x0)) {
            payable(msg.sender).transfer(_tokenAmount);
        } else {
            IERC20(_tokenAddress).safeTransfer(address(msg.sender), _tokenAmount);
        }

        emit AdminTokenRecovered(_tokenAddress, _tokenAmount);
    }

    /************************
    ** Internal Methods
    *************************/
    /*
     * @notice Update reward variables of the given pool to be up-to-date.
     */

    function _transferToken(address _token, address _to, uint256 _amount) internal {
        if(_token == address(0x0)) {
            payable(_to).transfer(_amount);
        } else {
            IERC20(_token).transfer(_to, _amount);
        }
    }

    receive() external payable {}
}

