    "contracts/Crowdsale/CCOFPublicsale.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.7;

import "./CappedCrowdsale.sol";
import "./TimedCrowdsale.sol";
import "./Crowdsale.sol";
import "./CrowdsaleAccessControl.sol";
import "../Inventory/CCOFInventory.sol";

contract CCOFPublicsale is
    Crowdsale,
    TimedCrowdsale,
    CappedCrowdsale,
    CrowdsaleAccessControl
{
    /**
     * @dev txTokenLimit token limit per transaction
     */
    uint256 public immutable txTokenLimit = 2;

    mapping(address => uint256) public lastBlockNumber;

    CCOFInventory public inventory;

    /**
     * @dev owner can set new opening time through the setter function
     * @param _openingTime new opening time of the crowdsale
     */

    function setOpeningTime(uint256 _openingTime) public onlyOwner {
        openingTime = _openingTime;
    }

    /**
     * @dev owner can set new closing time through the setter function
     * @param _closingTime new closing time of the crowdsale
     */

    function setClosingTime(uint256 _closingTime) public onlyOwner {
        closingTime = _closingTime;
    }

    /**
     * @dev owner can set new price through the setter function
     * @param _price new price  of the token
     */

    function setPrice(uint256 _price) public onlyOwner {
        price = _price;
    }

    /**
     * Constructor.
     * @param _inventory addresss of the inventory contract
     * @param _cap total supply of tokens
     * @param _openingTime start time of the crowdsale
     * @param _closingTime end time of the crowdsale
     * @param _price price of the token.
     * @param _treasury wallet where all collected funds are stored
     */

    constructor(
        address _inventory,
        uint256 _cap,
        uint256 _openingTime,
        uint256 _closingTime,
        uint256 _price,
        address payable _treasury
    )
        CrowdsaleAccessControl()
        Crowdsale(_price, _treasury)
        CappedCrowdsale(_cap)
        TimedCrowdsale(_openingTime, _closingTime)
    {
        inventory = CCOFInventory(_inventory);
    }

    /**
     * @dev Executed when a user is buying tokens
     */
    function buyTokensPublicsale() external payable {
        require(
            lastBlockNumber[msg.sender] < block.number,
            "ERROR: multiple mint transactions"
        );
        require(msg.sender == tx.origin, "ERROR: msg sender not EOA");
        lastBlockNumber[msg.sender] = block.number;
        buyTokens();
    }

    function _getTokenAmount(
        address _beneficiary,
        uint256 _weiAmount,
        uint256 _price
    ) internal override(Crowdsale) returns (uint256) {
        uint256 tokenAmount = super._getTokenAmount(
            _beneficiary,
            _weiAmount,
            _price
        );

        if ((tokenSold + tokenAmount) > cap) {
            tokenAmount = cap - tokenSold;
        }
        if (tokenAmount > txTokenLimit) {
            return txTokenLimit;
        }

        return tokenAmount;
    }

    function _preValidatePurchase(address _beneficiary, uint256 _tokenAmount)
        internal
        override(CappedCrowdsale, TimedCrowdsale, Crowdsale)
        whenNotPaused
    {
        require(
            _tokenAmount <= txTokenLimit,
            "ERROR:Max 2 mints in one transaction"
        );

        super._preValidatePurchase(_beneficiary, _tokenAmount);
    }

    function _updatePurchasingState(
        address _beneficiary,
        uint256 _tokenAmount,
        uint256 _weiAmount
    ) internal override {
        super._updatePurchasingState(_beneficiary, _tokenAmount, _weiAmount);
    }

    function _deliverToken(address _beneficiary, uint256 tokenAmount)
        internal
        override
    {
        inventory.mint(_beneficiary, tokenAmount);
    }
}
"
    },
    "contracts/Crowdsale/TimedCrowdsale.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.7;

import "./Crowdsale.sol";

abstract contract TimedCrowdsale is Crowdsale {
    /**
     * @dev openingTime Time when the sale starts
     */

    uint256 public openingTime;

    /**
     * @dev closingTime Time when the sale ends
     */
    uint256 public closingTime;

    /**
     *@dev reverts if not in Crowdsale time range
     **/

    modifier onlyWhileOpen() {
        require(
            block.timestamp >= openingTime,
            "ERROR: Crowdsale hasn't started yet"
        );
        require(block.timestamp <= closingTime, "ERROR: Crowdsale Over");

        _;
    }

    /**
     * @dev Constructor, takes crowdsale opening and closing times.
     * @param _openingTime opening time
     * @param _closingTime  closing time
     */

    constructor(uint256 _openingTime, uint256 _closingTime) {
        require(_closingTime >= _openingTime);

        openingTime = _openingTime;
        closingTime = _closingTime;
    }

    /**
     * @dev Checks whether the period in which the crowdsale is open has already elapsed.
     * @return Whether crowdsale period has elapsed
     */

    function crowdsaleHasClosed() public view returns (bool) {
        return block.timestamp > closingTime;
    }

    /**
     * @dev Extend parent behavior requiring to be within contributing period
     *@param _beneficiary addr receiving the token
     * @param _tokenAmount Number of tokens sold
     */

    function _preValidatePurchase(address _beneficiary, uint256 _tokenAmount)
        internal
        virtual
        override
        onlyWhileOpen
    {
        super._preValidatePurchase(_beneficiary, _tokenAmount);
    }
}
"
    },
    "contracts/Inventory/CCOFInventory.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.7;

import "./InventoryAccessControl.sol";
import "erc721a/contracts/ERC721A.sol";
import "erc721a/contracts/extensions/ERC721AQueryable.sol";

contract CCOFInventory is ERC721A, InventoryAccessControl, ERC721AQueryable {
    //Uniform Resource Identifier (URI) for `tokenId` token.
    string public baseURI;

    /**
     * Constructor.
     * @param _name the name of the ERC721A token.
     * @param _symbol the symbol to represnt the token.
     * @param _baseURI the URI of the token.
     */
    constructor(
        string memory _name,
        string memory _symbol,
        string memory _baseURI
    ) ERC721A(_name, _symbol) InventoryAccessControl() {
        baseURI = _baseURI;
    }

    /**
     * @dev Safely mints `quantity` tokens and transfers them to `_beneficiary`.
     *
     * Requirements:
     *  -minter address must have minter role
     *  -pause status must be false
     * Emits a {Transfer} event for each mint.
     */
    function mint(address _beneficiary, uint256 quantity) public onlyMinter {
        _safeMint(_beneficiary, quantity);
    }

    /**
     * @dev Public function with onlyOwner role to change the baseURI value
     */
    function setBaseURI(string memory _newBaseURI) public onlyOwner {
        baseURI = _newBaseURI;
    }

    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    /**
     * @dev override the function by adding `whenNotPaused` modifier
     */
    function _beforeTokenTransfers(
        address from,
        address to,
        uint256 startTokenId,
        uint256 quantity
    ) internal override whenNotPaused {}

    /**
     * @dev Destroys `tokenId`.
     * The approval is cleared when the token is burned.
     *
     * Requirements:
     *
     * - `tokenId` must exist.
     * - `burnPaused` status must be `false`
     *
     * Emits a {Transfer} event.
     */
    function burn(uint256 tokenId) public whenBurnNotPaused {
        _burn(tokenId, true);
    }

    /**
     * @dev Check Interface of ERC721A
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC721A)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
"
    },
    "contracts/Crowdsale/CappedCrowdsale.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.7;

import "./Crowdsale.sol";

abstract contract CappedCrowdsale is Crowdsale {
    /**
     *@dev cap Max number of tokens
     */
    uint256 public cap;

    /**
     * @dev Constructor, takes maximum amount of tokens available in the crowdsale.
     * @param _cap Max number of tokens that can be purchased in the current phase
     */
    constructor(uint256 _cap) {
        require(_cap > 0);
        cap = _cap;
    }

    /**
     * @dev Checks whether the cap has been reached.
     * @return bool Whether the cap was reached
     */
    function capReached() public view returns (bool) {
        return tokenSold >= cap;
    }

    /**
     * @dev Extend parent behavior requiring to be within contributing period
     *@param _beneficiary addr receiving the token
     * @param _tokenAmount Number of tokens sold
     */

    function _preValidatePurchase(address _beneficiary, uint256 _tokenAmount)
        internal
        virtual
        override
    {
        require(!capReached(), "ERROR:Cap Reached Crowdsale Over");
        super._preValidatePurchase(_beneficiary, _tokenAmount);
    }
}
"
    },
    "contracts/Crowdsale/Crowdsale.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.7;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

abstract contract Crowdsale is ReentrancyGuard {
    /**
     * @dev weiRaised Total wei Raised in the current phase of crowdsale
     */
    uint256 public weiRaised;

    /**
     * @dev tokenSold Total token sold in the current phase of crowdsale
     */
    uint256 public tokenSold;

    /**
     * @dev price Price per token for crowdsale in wei
     */
    uint256 public price;

    /**
     * @dev treseaury wallet where all weiRaised will be collected
     */
    address payable treasury;

    /*Event for logging Token Purchase
     * @param buyer who is buying the token
     * @param value wei paid for purchase
     * @param tokenAmount number of tokens purchased
     */

    event TokenPurchase(
        address indexed buyer,
        uint256 value,
        uint256 tokenAmount
    );

    constructor(uint256 _price, address payable _treasury) {
        require(_treasury != address(0));

        treasury = _treasury;
        price = _price;
    }

    /** @dev fallback functiion **DO NOT OVERRIDE**/
    //When no other function matches

    fallback() external payable {
        revert();
    }

    /** @dev Executed when a user is buying tokens.
    -Based upon the value sent by the user, tokenAmount is calculated.
    -This function also prevalidates the purchase by doing checks.
    -The purchase state is updated with total tokens sold and adding purchase values to the wei Raised.
    -The purchase is processed and delivered to the user after performing mint.
    -The function emits an event TokenPurchase where the address of the user, value spent and the number of tokens purchased is mentioned.
    -The value spent by the user is then forwarded to the treasury wallet.
    -If any value is left for example, value not enough to buy a single token or excess value sent in regards to purchase limit of the user or tranasaction token limt, the value is refunded back to the user.
    */

    function buyTokens() internal nonReentrant {
        address _beneficiary = msg.sender;
        uint256 weiAmount = msg.value;

        uint256 tokenAmount = _getTokenAmount(_beneficiary, weiAmount, price);

        _preValidatePurchase(_beneficiary, tokenAmount);

        uint256 weiUsed = tokenAmount * price;

        _updatePurchasingState(_beneficiary, tokenAmount, weiUsed);

        _processPurchase(_beneficiary, tokenAmount);
        emit TokenPurchase(msg.sender, weiUsed, tokenAmount);

        _forwardFunds(weiUsed);
        _refund(msg.sender, (weiAmount - weiUsed));
    }

    /**
     * @dev Override to extend the way in which ether is converted to tokens.
     * @param _beneficiary Address receiving the token
     * @param _weiAmount Value in wei to be converted into tokens
     * @param _price Price of each token in wei
     * @return Number of tokens that can be purchased with the specified _weiAmount
     */

    function _getTokenAmount(
        address _beneficiary,
        uint256 _weiAmount,
        uint256 _price
    ) internal virtual returns (uint256) {
        uint256 tokenAmount = _weiAmount / _price;

        return tokenAmount;
    }

    /**
     * @dev Validation of an incoming purchase.Use require statements to revert state when conditions are not met
     * @param _beneficiary address performing the token purchase
     * @param _tokenAmount Number of tokens to purchase
     */

    function _preValidatePurchase(address _beneficiary, uint256 _tokenAmount)
        internal
        virtual
    {
        require(_beneficiary != address(0), "ERROR: Zero Address");
        require(_tokenAmount >= 1, "ERROR: Token Amount Zero");
    }

    /**
     * @dev Override for extensions that require an internal state to check for validity (current user contributions, etc.)
     * @param _beneficiary Address receiving the tokens
     * @param _tokenAmount number of token purchased
     * @param _weiAmount Value in wei involved in the purchase
     */
    function _updatePurchasingState(
        address _beneficiary,
        uint256 _tokenAmount,
        uint256 _weiAmount
    ) internal virtual {
        weiRaised = weiRaised + _weiAmount;
        tokenSold = tokenSold + _tokenAmount;
    }

    /**
     * @dev Executed when a purchase is validated and is ready to be executed. Not neccesarily emits/sends Tokens.
     * @param _beneficiary addr performing the token purchase
     * @param _tokenAmount Number of tokens purchased
     */

    function _processPurchase(address _beneficiary, uint256 _tokenAmount)
        internal
        virtual
    {
        _deliverToken(_beneficiary, _tokenAmount);
    }

    /** @dev Source of token. Override this method to modify the way in which Crowdsale ultimately gets and sends its tokens.
     * @param _beneficiary addr performing the token purchase
     * @param _tokenAmount Number of tokens purchased
     */

    function _deliverToken(address _beneficiary, uint256 _tokenAmount)
        internal
        virtual
    {
        //optional override
    }

    /**
    *@dev Determines how eth is stored/forwarded on purchases
     @param _value amount to be transfered to treasury wallet
    */

    function _forwardFunds(uint256 _value) internal virtual {
        (bool sent, ) = treasury.call{value: _value}("");
        require(sent, "Failed to send ether");
    }

    /**
     *@dev Determines how eth is refunded to the beneficiary
     *@param _beneficiary address receiving the refund
     *@param _amount amount to be refunded
     */

    function _refund(address _beneficiary, uint256 _amount) internal virtual {
        if (_amount > 0) {
            (bool sent, ) = _beneficiary.call{value: _amount}("");
            require(sent, "Failed to send ether");
        }
    }
}
"
    },
    "contracts/Crowdsale/CrowdsaleAccessControl.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.7;

import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

abstract contract CrowdsaleAccessControl is Ownable, Pausable {
    /**
     * @dev Pauses the crowdsale contract.
     * Only Owner can pause the contract.
     * Mint cannot be performed while the contract is paused
     */
    function pause() public onlyOwner {
        _pause();
    }

    /**
     * @dev unPauses the crowdsale contract.
     * Only Owner can unpause the contract.
     * unPaused by default.
     */
    function unpause() public onlyOwner {
        _unpause();
    }
}
"
