pragma solidity ^0.8.4;




contract HallofGrace is ERC721AQueryable, Ownable {
  using Strings for uint256;

  string uriPrefix = "https://bafybeib4esyrlhn5gwebsaaodrybo5xnql7dgugqepzubbyftln4o5bjym.ipfs.nftstorage.link/";

  constructor() ERC721A("Hall of Grace by Tarcosa", "HALL") {}

  
  function devMint(uint256 _mintAmount, address _receiver) public onlyOwner {
    _safeMint(_receiver, _mintAmount);
  }


  function _startTokenId() internal pure override returns (uint256) {
    return 1;
  }

  function batchAirdrop(address[] memory recipients) public onlyOwner {
    for(uint i = 0; i< recipients.length; i++){
      _safeMint(recipients[i], 1);
     }
}

  function tokenURI(uint256 _tokenId) public view virtual override returns (string memory) {
    require(_exists(_tokenId), 'ERC721Metadata: URI query for nonexistent token');


    string memory currentBaseURI = _baseURI();
    return bytes(currentBaseURI).length > 0
        ? string(abi.encodePacked(currentBaseURI, _tokenId.toString(), ".json"))
        : '';
  }

  function _baseURI() internal view virtual override returns (string memory) {
    return uriPrefix;
  }

  function setUriPrefix(string memory _uriPrefix) public onlyOwner {
    uriPrefix = _uriPrefix;
  }

  function withdraw() public onlyOwner {
    (bool os, ) = payable(owner()).call{value: address(this).balance}('');
    require(os);
  }
}

